mapper.update:
update dist_order_info
task_id = #{taskId}, job_no = #{jobNo}, user_id = #{userId}, job_status = #{jobStatus}, operation_time = #{operationTime}, create_time = #{createTime}, expire_time = #{expireTime}, order_id = #{orderId}, dist_weight = #{distWeight},
where id = #{id}

mapper.insert:
insert into dist_order_info(
task_id , user_id , job_no , job_status , operation_time , create_time , expire_time , order_id , dist_weight ,
)values(
#{taskId} , #{userId} , #{jobNo} , #{jobStatus} , #{operationTime} , #{createTime} , #{expireTime} , #{orderId} , #{distWeight} ,
)

mapper.select:
SELECT dist_order_info.id as id, task_info.task_id as taskId, task_info.title as title, task_info.dep as dep, task_info.arr as ass, dist_order_info.create_time as startTime, dist_order_info.expire_time as endTime, task_info.distance as distance, task_info.unit_price as price, dist_order_info.job_status as status, dist_order_info.dist_weight as weight FROM dist_order_info inner join task_info on dist_order_info.task_id = task_info.task_id where dist_order_info.user_id = #{userId}
and dist_order_info.id < #{curId}
and dist_order_info.id <= (select max(id) from dist_order_info limit 1)
order by dist_order_info.id desc limit #{pgSize};
