mapper.select:
select

from lorry_info
where status!=0 and user_id=#{userId} order by id desc;

mapper.update:
update lorry_info
lorry_type = #{lorryType}, carry_weight = #{carryWeight}, plate_number = #{plateNumber}, frame_number = #{frameNumber}, engine_no = #{engineNo}, over_carry_weight = #{overCarryWight}, car_lice_owner = #{carLiceOwner},
where lorry_id = #{lorryId}
