mapper.update:
update order_info
task_id = #{taskId}, lorry_id = #{lorryId}, user_id = #{userId}, supplier_id = #{supplierId}, delivery_id = #{deliveryId}, distance = #{distance}, unit_distance = #{unitDistance}, carry_weight = #{carryWeight}, order_weight = #{orderWeight}, succ_weight = #{succWeight}, overweight = #{overweight}, unit_weight = #{unitWeight}, accept_time = #{acceptTime}, start_time = #{startTime}, must_finish_time = #{mustFinishTime}, send_out_time = #{sendOutTime}, estimate_finish_time = #{estimateFinishTime}, finish_time = #{finishTime}, send_out_code = #{sendOutCode}, send_out_user_id = #{sendOutUserId}, receive_code = #{receiveCode}, receiver_user_id = #{receiverUserId}, status = #{status}, unexpect = #{unexpect}, cancel_time = #{cancelTime}, cancel_reason = #{cancelReason}, ext_info = #{extInfo}, create_time = #{createTime}, operate_time = #{operateTime}, receiveTime = #{operateTime},
where id = #{id}

update order_info
set status=#{status}
,carry_weight=#{carryWeight} 
,receive_time=#{receiveTime} 
,send_out_time=#{sendOutTime} 

where order_id = #{orderId} and
status=#{oldStatus} and
supplier_id=#{supplierId}

mapper.select:
SELECT o.order_id as orderId, o.task_id as taskId, t.title as title, t.dep as dep, t.arr as arr, t.start_time as startValidDate, t.end_time as endValidDate, l.id as lorryId, l.plate_number as plateNumber, l.carry_weight as carryWeight, o.verify_code as verifyCode, o.receive_time as startTime from order_info o left join task_info t on o.task_id=t.task_id left join lorry_info l on l.id=o.lorry_id where 1=1
and o.order_id=#{orderId}
and o.send_out_code=#{verifyCode} and o.supplier_id=#{supplierId}
and o.receive_code=#{verifyCode} and o.delivery_id=#{supplierId}
